using Orbit.Models;
using System.Collections.ObjectModel;

namespace Orbit.Services.Planets
{
    public class PlanetsService : IPlanetsService
    {
        public ObservableCollection<Planet> GetPlanets()
        {
            return new ObservableCollection<Planet>
            {
                new Planet { PlanetId = "mercury", Name = "Mercurio", Image = "ms-appx:///Assets/Planets/Mercury/Mercury.jpg", HeroImage = "ms-appx:///Assets/Planets/Mercury/Mercury Hero.jpg", Diameter = 4878, SunDistance = 57.9, Temperature = 167, Description = "Mercurio es el planeta del sistema solar más próximo al Sol y el más pequeño. Forma parte de los denominados planetas interiores o terrestres y carece de satélites al igual que Venus. Se conocía muy poco sobre su superficie hasta que fue enviada la sonda planetaria Mariner 10 y se hicieron observaciones con radar y radiotelescopios." },
                new Planet { PlanetId = "venus", Name = "Venus", Image = "ms-appx:///Assets/Planets/Venus/Venus.jpg",  HeroImage = "ms-appx:///Assets/Planets/Venus/Venus Hero.jpg", Diameter = 12100, SunDistance = 108.2, Temperature = 464, Description = "Venus es el segundo planeta del sistema solar en orden de distancia desde el Sol, y el tercero en cuanto a tamaño, de menor a mayor. Al igual que Mercurio, carece de satélites naturales. Recibe su nombre en honor a Venus, la diosa romana del amor. Se trata de un planeta de tipo rocoso y terrestre, llamado con frecuencia el planeta hermano de la Tierra, ya que ambos son similares en cuanto a tamaño, masa y composición, aunque totalmente diferentes en cuestiones térmicas y atmosféricas. La órbita de Venus es una elipse con una excentricidad de menos del 1 %, formando la órbita más circular de las de todos los planetas; apenas supera la de Neptuno. Su presión atmosférica es 90 veces superior a la terrestre; es, por tanto, la mayor presión atmosférica de las de todos los planetas rocosos del sistema solar." },
                new Planet { PlanetId = "earth", Name = "Tierra", Image = "ms-appx:///Assets/Planets/Earth/Earth.jpg",  HeroImage = "ms-appx:///Assets/Planets/Earth/Earth Hero.jpg", Diameter = 12756, SunDistance = 149.6, Temperature = 15, Description = "La Tierra (del latín Terra, deidad romana equivalente a Gea, diosa griega de la feminidad y la fecundidad) es un planeta del sistema solar que gira alrededor de su estrella —el Sol— en la tercera órbita más interna. Es el más denso y el quinto mayor de los ocho planetas del sistema solar. También es el mayor de los cuatro terrestres." },
                new Planet { PlanetId = "mars", Name = "Marte", Image = "ms-appx:///Assets/Planets/Mars/Mars.jpg",  HeroImage = "ms-appx:///Assets/Planets/Mars/Mars Hero.jpg", Diameter = 6786, SunDistance = 227.9, Temperature = -20, Description = "Marte es el cuarto planeta del sistema solar en orden de distancia al Sol. Llamado así por el dios de la guerra de la mitología romana Marte, recibe a veces el apodo de planeta rojo debido a la apariencia rojiza que le confiere el óxido de hierro que domina su superficie. Tiene una atmósfera delgada, formada por dióxido de carbono, y dos satélites: Fobos y Deimos. Forma parte de los llamados planetas telúricos (de naturaleza rocosa, como la Tierra) y es el planeta interior más alejado del Sol. Es, en muchos aspectos, el más parecido a la Tierra." },
                new Planet { PlanetId = "jupiter", Name = "Júpiter", Image = "ms-appx:///Assets/Planets/Jupiter/Jupiter.jpg",  HeroImage = "ms-appx:///Assets/Planets/Jupiter/Jupiter Hero.jpg", Diameter = 143200, SunDistance = 778.6, Temperature = -65, Description = "Júpiter es el quinto planeta del sistema solar. Forma parte de los denominados planetas exteriores o gaseosos. Recibe su nombre del dios romano Júpiter (Zeus en la mitología griega). Se trata del planeta que ofrece un mayor brillo a lo largo del año dependiendo de su fase. Es, además, después del Sol, el mayor cuerpo celeste del sistema solar, con una masa casi dos veces y media la de los demás planetas juntos (con una masa 318 veces mayor que la de la Tierra y tres veces mayor que la de Saturno, además de ser en cuanto a volumen, 1317 veces más grande que la Tierra)." },
                new Planet { PlanetId = "saturn", Name = "Saturno", Image = "ms-appx:///Assets/Planets/Saturn/Saturn.jpg",  HeroImage = "ms-appx:///Assets/Planets/Saturn/Saturn Hero.jpg", Diameter = 120536, SunDistance = 1433.5, Temperature = -110, Description = "Saturno es el sexto planeta del sistema solar, el segundo en tamaño y masa después de Júpiter y el único con un sistema de anillos visible desde nuestro planeta. Su nombre proviene del dios romano Saturno. Forma parte de los denominados planetas exteriores o gaseosos. El aspecto más característico de Saturno son sus brillantes anillos. Antes de la invención del telescopio, Saturno era el más lejano de los planetas conocidos y, a simple vista, no parecía luminoso ni interesante." },
                new Planet { PlanetId = "uranus", Name = "Urano", Image = "ms-appx:///Assets/Planets/Uranus/Uranus.jpg",  HeroImage = "ms-appx:///Assets/Planets//Uranus/Uranus Hero.jpg", Diameter = 51118, SunDistance = 2872.5, Temperature = -195, Description = "Urano es el séptimo planeta del sistema solar, el tercero de mayor tamaño, y el cuarto más masivo. Se llama así en honor de la divinidad griega del cielo Urano (del griego antiguo «Οὐρανός») el padre de Crono (Saturno) y el abuelo de Zeus (Júpiter). Aunque es detectable a simple vista en el cielo nocturno, no fue catalogado como planeta por los astrónomos de la antigüedad debido a su escasa luminosidad y a la lentitud de su órbita." },
                new Planet { PlanetId = "neptune", Name = "Nepturno", Image = "ms-appx:///Assets/Planets/Neptune/Neptune.jpg",  HeroImage = "ms-appx:///Assets/Planets/Neptune/Neptune Hero.jpg", Diameter = 49528, SunDistance = 4495.1, Temperature = -200, Description = "Neptuno es el octavo planeta en distancia respecto al Sol y el más lejano del sistema solar. Forma parte de los denominados planetas exteriores o gigantes gaseosos, y es el primero que fue descubierto gracias a predicciones matemáticas. Su nombre fue puesto en honor al dios romano del mar —Neptuno—, y es el cuarto planeta en diámetro y el tercero más grande en masa. Su masa es diecisiete veces la de la Tierra y ligeramente más masivo que su planeta «gemelo» Urano, que tiene quince masas terrestres y no es tan denso." },
                new Planet { PlanetId = "pluton", Name = "Plutón", Image = "ms-appx:///Assets/Planets/Pluto/Pluto.jpg",  HeroImage = "ms-appx:///Assets/Planets/Pluto/Pluto Hero.jpg", Diameter = 2400, SunDistance = 5906.4, Temperature = -225, Description = "Plutón o (134340) Pluto es un planeta enano del sistema solar situado a continuación de la órbita de Neptuno. Su nombre se debe al dios mitológico romano Plutón (Hades según los griegos)." },
            };
        }
    }
}
